from services.dicc_creator import creator_diction

def test_one():
   assert creator_diction(["titulo","primer","segundo","postre","descripción","ingredientes","stock","precio","reviews"]) == ([{"Nombre":'titulo',"Primero":'primer',"Segundo": 'segundo',"Postre": 'postre',"Descripción": 'descripción',"Ingredientes":'ingredientes',"Stock": 'stock',"Precio": 'precio',"Reviews":'reviews'}])

